import React from 'react';
import {useForm, useStep} from 'react-hooks-helper';
import Names from './step'

const defaultData ={
    firstName:"",
    lastName:"",
   nickName:"",
};

const steps=[
    {id:'names'},
    {id:'address'},
    {id:'contact'},
    {id:'review'},
    {id:'submit'},
    
]

const MultiStepForm = () => {
    const [formData, setForm]= useForm(defaultData);
    const { step, navigation} = useStep({
        steps,
        initialStep:0,
    });

    switch(step.id){
        case "name": return <Names />
    }

    return (
        <div>
            <h1>Student Form</h1>
        </div>
    )
}

export default MultiStepForm
