import React from 'react';
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetail from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';


const Review = ({ formData, navigation }) => {
    <h1>Confirmation</h1>
      const {go }= navigation;
    const {
        firstName,
        lastName,
        matrialStatus,
        address,
        country,
        city,
        state,
        zip,
        phone,
        email,
      } = formData;
    return (
      <Container maxWidth="sm">
          <h1>Review</h1>
          

            <RenderAccordion summary="Basic Details" go={go} details={[
                            { 'Address': firstName },
                            { 'Last Name': lastName },
                            { 'Matrial Status':matrialStatus },
            ]}/>
              <RenderAccordion summary="Accomodation Details" go={go} details={[
                  { 'Address': address },
                  {'Country':country},
                  { 'State': state },
                  { 'City': city },
                  { 'Pin': zip },
            ]}/>

            <RenderAccordion summary="Project Details" go={go} details={[
                             { 'Phone': phone },
                             { 'Email': email },
            ]}/>

            <Button
                color="primary" variant="contained" style={{ marginTop: '1.5rem' }}   onClick={() => go('submit')}>Submit</Button>
      </Container>
    )
}

export default Review


export const RenderAccordion =({summary, details , go})=>(
    <Accordion>
        <AccordionSummary  expandIcon={<ExpandMoreIcon/>}>{summary}</AccordionSummary>
        <AccordionDetail>
            <div>
                    {details.map((data, index)=>{
                        const objKey= Object.keys(data)[0];
                        const objValue =data[Object.keys(data)[0]];

                        return <ListItemText key={index}>{`${objKey}: ${objValue}`}</ListItemText>
                    })}
                    <IconButton color="primary"component="span"   onClick={() => go(`${summary.toLowerCase()}`)}><EditIcon /></IconButton>
            </div>
        </AccordionDetail>
    </Accordion>
)
